import React, { useState } from "react";
import Display from "../views/Display";
import Button from "../views/Button";

const Helpers = (props) => {
  const [time, setTime] = useState({ milisec: 0, sec: 0, min: 0, hr: 0 });
  const [interval, setI] = useState();
  const [state, setState] = useState(0);

  const start = () => {
    run();
    setState(0);
    setI(setInterval(run, 10));
  };

  let newMilisec = time.milisec,
    newSec = time.sec,
    newMin = time.min,
    newHr = time.hr;

  const run = () => {
    if (newMin === 60) {
      newHr++;
      newMin = 0;
    }
    if (newSec === 60) {
      newMin++;
      newSec = 0;
    }
    if (newMilisec === 100) {
      newSec++;
      newMilisec = 0;
    }
    newMilisec++;
    return setTime({
      milisec: newMilisec,
      sec: newSec,
      min: newMin,
      hr: newHr,
    });
  };

  const stop = () => {
    clearInterval(interval);
    setState(0); //not same
  };

  const reset = () => {
    clearInterval(interval);
    setState(0);
    setTime({ milisec: 0, sec: 0, min: 0, hr: 0 });
  };

  const resume = () => start();

  return (
    <div>
      <div>
        <display time={time}></display>
        <button func={start}>Start</button>
        <button func={stop}>Pause</button>
        <button func={reset}>Reset</button>
      </div>
    </div>
  );
};

export default Helpers;
