import React, { useState } from "react";

const CountForm = (props) => {
  const [enteredHour, setEnteredHour] = useState("");
  const [enteredMinute, setEnteredMinute] = useState("");
  const [enteredSecond, setEnteredSecond] = useState("");
  const [enteredMiliSecond, setEnteredMiliSecond] = useState("");

  const hourChangeHandler = (event) => {
    setEnteredHour(event.target.value);
  };
  const minuteChangeHandler = (event) => {
    setEnteredMinute(event.target.value);
  };
  const secondChangeHandler = (event) => {
    setEnteredSecond(event.target.value);
  };
  const milisecondChangeHandler = (event) => {
    setEnteredMiliSecond(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const timeData = {
      milisec: parseInt(enteredMiliSecond),
      sec: parseInt(enteredSecond),
      min: parseInt(enteredMinute),
      hr: parseInt(enteredHour),
    };

    props.times(timeData);
    setEnteredHour("");
    setEnteredMinute("");
    setEnteredSecond("");
    setEnteredMiliSecond("");
  };

  const defaultValue = React.createClass({
    getInitialState() {
      return {
        userCount: 5,
      };
    },
  });
  return (
    <form onSubmit={submitHandler}>
      <div>
        <label>Hour</label>
        <input
          type="number"
          value={enteredHour}
          min="0"
          step="1"
          max="99"
          defaultValue={defaultValue}
          onChange={hourChangeHandler}
        />
      </div>
      <div>
        <label>Minute</label>
        <input
          type="number"
          value={enteredMinute}
          min="0"
          step="1"
          max="60"
          defaultValue={defaultValue}
          onChange={minuteChangeHandler}
        />
      </div>
      <div>
        <label>Second</label>
        <input
          type="number"
          value={enteredSecond}
          min="0"
          step="1"
          max="60"
          defaultValue={defaultValue}
          onChange={secondChangeHandler}
        />
      </div>
      <div>
        <label>MiliSecond</label>
        <input
          type="number"
          value={enteredMiliSecond}
          min="0"
          step="1"
          max="99"
          defaultValue={defaultValue}
          onChange={milisecondChangeHandler}
        />
      </div>
      <button type="submit">Set Timer</button>
    </form>
  );
};

export default CountForm;
